mapper.select:
select

from t_dept_order_report a ,sys_dept b where a.dept_id = b.dept_id

    and a.dept_id in (${join})


    and report_date >= #{beginTime}


    and report_date <= #{endTime}


    and a.dept_id = #{deptId}


    and channel_no = #{channelNo}

order by report_date  desc

