mapper.select:
	select

 	from t_dept_account_flow where 1=1

        and create_time >= CONCAT(#{beginTime},' 00:00:00')


    	and create_time <= CONCAT(#{endTime},' 23:59:59')

    and dept_id = #{deptId}

    	and biz_type = #{bizTypeName}


    	and direction = #{directionName}

    order by create_time  desc

	select

 	from t_dept_account_flow where 1=1

        and dept_id in (${join})


        and create_time >= CONCAT(#{beginTime},' 00:00:00')


    	and create_time <= CONCAT(#{endTime},' 23:59:59')

    and dept_id = #{deptId}

    	and biz_type = #{bizTypeName}


    	and direction = #{directionName}


    	and dept_id like CONCAT(#{deptType},'%')

    order by create_time  desc
