mapper.select:
	select

 	from t_mch_cash_flow where 1=1

        and cash_id = #{cashId}


        and out_trade_no = #{outTradeNo}


        and cash_status = #{cashStatus}


        and mch_name = #{mchName}


        and bank_card_no = #{bankCardNo}


        and create_time >= CONCAT(#{beginTime},' 00:00:00')


    	and create_time <= CONCAT(#{endTime},' 23:59:59')

    and dept_id = #{deptId}
    order by create_time  desc

	select

 	from t_mch_cash_flow where 1=1

        and dept_id = #{deptId}


        and cash_id = #{cashId}


        and out_trade_no = #{outTradeNo}


        and cash_status = #{cashStatus}


        and mch_name = #{mchName}


        and bank_card_no = #{bankCardNo}


        and create_time >= CONCAT(#{beginTime},' 00:00:00')


    	and create_time <= CONCAT(#{endTime},' 23:59:59')

    order by create_time  desc

select count(*) as cashNum,sum(cash_amount) as cashAmount, sum(dept_amount) as deptAmount,sum(cost_amount) as costAmount from t_mch_cash_flow where 1=1
and create_time >= CONCAT(#{beginTime},' 00:00:00')
and create_time <= CONCAT(#{endTime},' 23:59:59')
and dept_id = #{deptId}
and cash_status = #{cashStatus}
